module Users {

	export class User extends BaseCollection<User> {

		static db = new DataCollection<Users.User>({
			source: User,
			name: 'Users.User',
			connection: <any>Meteor,
			relations: {
				id: {
					index: {
						unique: true,
						sparse: true
					}
				},
				profile: {
					target: 'Users.Profile'
				}
			},
			allow: {
				insert: (userId: string, doc: Object): boolean => {
					return true;
				},
				update: (userId: string, doc: Object, fieldNames: string[], modifier): boolean => {
					return true;
				},
				remove: (userId: string, doc: Object): boolean => {
					return true;
				}
			},
			deny: {}
		});

		public username: string;
		public emails: { address: string }[];
		public profile: OneRelation<Profile>;
		public services: any;

		/**
		 * Checks weather the username is available or not.
		 *
		 * @param username Username to check
		 */
		static checkUsernameAvailability(username: string, site?: string, callback?: (result: boolean) => void): boolean {
			return false;
		}

		/**
		 * Checks weather the emails is available or not.
		 *
		 * @param email Emailto check
		 */
		static checkEmailAvailability(email: string, site?: string, callback?: (result: boolean) => void): boolean {
			return false;
		}

		/**
		 * Log the user in with a password.
		 *
		 * @param user Either a string interpreted as a username or an email;
		 *             or an object with a single key: email, username or id.
		 * @param password Either a string interpreted as a username or an email;
		 *                 or an object with a single key: email, username or id.
		 * @param callback Optional callback. Called with no arguments on success,
		 *                 or with a single Error argument on failure.
		 */
		static loginWithPassword(username: string, password: string, callback: (err?) => void) {}

		/**
		 * Request a forgot password email.
		 *
		 * @param email The email address to send a password reset link.
		 * @param callback Optional callback. Called with no arguments on success, or with a single Error argument on failure.
		 */
		static forgotPassword(email: string, callback: (error?) => void) { }

		/**
		 * Server only. Forcibly change the password for a user.
		 *
		 * @param newPassword A new password for the user.
		 */
		setPassword(newPassword: string) { }

		/**
		 * Server only. Send an email with a link the user can use to reset their password.
		 *
		 * The token in this email should be passed to Accounts.resetPassword.
		 * To customize the contents of the email, see Accounts.emailTemplates.
		 *
		 * @param email Optional. Which address of the user's to send the email to.
		 *              This address must be in the user's emails list. Defaults to the first email in the list.
		 */
		sendResetPasswordEmail(email: string) { }

		/**
		 * Server only. Send an email with a link the user can use to set their initial password.
		 *
		 * The token in this email should be passed to Accounts.resetPassword.
		 * To customize the contents of the email, see Accounts.emailTemplates.
		 *
		 * @param email Optional. Which address of the user's to send the email to.
		 *              This address must be in the user's emails list. Defaults to the first email in the list.
		 */
		sendEnrollmentEmail(email: string) { }

		static active(): User {
			if (!Meteor.userId()) {
				return undefined;
			}
			return this.db.findOne(Meteor.userId());
		}

		static create(userData: any, callback?: (err?) => void) {
			// server side
		}



	}

}

