module Users {
	export class ProfileDb extends DataCollection<Profile> {

		constructor(model) {
			super({
				source: model,
				name: 'Users.Profile',
				connection: Backend.Server,
				pickerOptions: {
					placeholder: 'User',
					icon: 'user',
					fields: ['username'],
					label: function(user: Profile){
						var site = user.site.fetch();
						if (site) {
							return user.username + '<small class="text-muted pull-right">' + site.name + '</small>';
						}
						else {
							return user.username;
						}
					}
				},
				fields: {
					signupTime: {
						type: FieldType.DATE
					},
					dateOfBirth: {
						type: FieldType.DATE
					}
				},
				relations: {
					site: {
						target: 'Backend.Site'
					},
					userId: {
						index: {
							unique: true,
							sparse: true
						}
					},
					wallets: {
						target: 'Banking.Wallet',
						linkedBy: 'user',
						many: true
					},
					tracker: {
						target: 'Affiliates.Tracker'
					},
					affiliate: {
						target: 'Affiliates.Affiliate'
					},
					roles: {
						target: 'Users.Access',
						many: true
					}
				},
				allow: {
					insert: (userId: string, doc: Object): boolean => {
						return false;
					},
					update: (userId: string, doc: any, fieldNames: string[], modifier): boolean => {
						if (fieldNames.indexOf('password') !== -1) {
							return false;
						}
						if (!userId) {
							return false;
						}

						var user = Profile.db.findOne({userId: userId});
						var globalAccess = user.hasAccess([Scope.GLOBAL, Scope.SITE], [Role.ADMIN]);

						// cant change my own rights unless i'm global admin
						if (!globalAccess && fieldNames.indexOf('roles') !== -1) {
							return false;
						}

						return (doc._id == user._id) || globalAccess;
					},
					remove: (userId: string, doc: Object): boolean => {
						return false;
					}
				},
				deny: {},
				validation: {
					firstName: {
						displayName: 'First name',
						required: true,
						rules: [{
							pattern: /^[a-zA-Z]+$/,
							errorMessage: 'users.firstName.formatError'
						}]
					},
					lastName: {
						displayName: 'Last name',
						required: true,
						rules: [{
							pattern: /^[a-zA-Z]+$/,
							errorMessage: 'users.lastName.formatError'
						}]
					},
					username: {
						displayName: 'Username',
						required: true,
						rules: [
							{
								pattern: /^[A-Za-z0-9$_+*-]{3,11}$/,
								errorMessage: 'users.username.formatError'
							},
							{
								callback: function(value, data, callback) {
									if (Meteor.isServer) {
										callback(null, User.checkUsernameAvailability(value, data.site.handle));
									}
									else {
										Meteor.call('checkUsernameAvailability', value, data.site ? data.site.handle : null, callback);
									}
								},
								errorMessage: 'users.username.existsError'
							}
						]
					},
					email: {
						displayName: 'users.email.name',
						required: true,
						rules: [
							{
								pattern: /^\S+@\S+\.\S+$/
							},
							{
								callback: function(value, data, callback) {
									if (Meteor.isServer) {
										callback(null, User.checkEmailAvailability(value, data.site.handle));
									}
									else {
										Meteor.call('checkEmailAvailability', value, data.site ? data.site.handle : null, callback);
									}
								},
								errorMessage: 'users.email.existsError'
							}
						]
					},
					phoneNumber: {
						displayName: 'users.phoneNumber.name',
						required: true,
							rules: [{
							pattern: /^[0-9+-]{6,20}$/,
							errorMessage: 'users.phoneNumber.formatError'
						}]
					},
					town: {
						displayName: 'users.town.name',
						required: true,
						rules: [{
							pattern: /^[a-zA-Z0-9_'+. -]{2,30}$/,
							errorMessage: 'users.town.formatError'
						}]
					},
					password: {
						displayName: 'users.password.name',
						required: true,
						rules: [{
							pattern: /^[A-Za-z0-9]{8,20}$/,
							errorMessage: 'users.password.formatError'
						}]
					},
					repeatPassword: {
						displayName: 'users.repeatPassword.name',
						required: true,
						rules: [{
							conditional: function (value, data) {
								return value === data.password;
							},
							errorMessage: 'users.repeatPassword.formatError'
						}]
					},
					securityAnswer: {
						displayName: 'users.securityAnswer.name',
						required: true,
						rules: [{
							pattern: /^[a-zA-Z0-9 ]{2,50}$/,
							errorMessage: 'users.securityAnswer.formatError'
						}]
					},
					dateOfBirth: {
						displayName: 'users.dateOfBirth.name',
						required: true,
						rules: [{
							conditional: (value): boolean => {
								var date = moment(value);
								if (!date) return false;
								var age = -date.diff(moment(), 'years');
								return 18 <= age && age <= 100;
							}
						}]
					},
					address: {
						displayName: 'users.address.name',
						required: true,
						rules: [
							{
								pattern: /^[a-zA-Z0-9_'+. -]{2,50}$/
							},
							{
								conditional: (value): boolean => {
									var country = User.active().profile.fetch().country.fetch().name;
//									var country = User.active().profile.fetch().country.fetch().name;
									return !(country === 'United Kingdom' && !/ [0-9]/.test(value));
								}
							}
						]
					},
					zip: {
						displayName: 'users.zip.name',
						required: true,
						rules: [
							{
								pattern: /^[a-zA-Z0-9_'+. -]{2,50}$/
							},
							{
								conditional: (value): boolean => {
									var country = Users.Profile.active().country.fetch().name;
									if (country === 'United Kingdom' && !/^[0-9a-zA-Z]{5,}$/.test(value))
										return false;
									else if ((country === 'Canada' || country === 'Netherlands') && !/^[0-9a-zA-Z]{2,}$/.test(value))
										return false;
									else
										return /^[0-9]{2,}$/.test(value);
								}
							}
						]
					},
					country: {
						displayName: 'users.country.name',
						required: true,
						rules: [{
							conditional: (value): boolean => {
								var countries = Countries.Country.db.find().fetch();
								var found = false;
								for (var i in countries) {
									var country = countries[i];
									if (value === country.name) {
										found = true;
										break;
									}
								}
								return found;
							}
						}]
					}
				}
			});
		}

	}
}

﻿/// <reference path=".server.d.ts" />
/// <reference path=".client.d.ts" />

module Users {

	export enum Role {
		ADMIN,
		SUPPORT
	}

	export enum Scope {
		GLOBAL,
		SITE,
		AFFILIATE
	}

	export enum VipStatus {
		NOTVIP,
		ACTIVE,
		INACTIVE
	}

	export var scopes = [];
	for (var i in Scope) {
		var name = Scope[i];
		if (typeof name == 'number') {
			continue;
		}
		name = name.charAt(0) + name.substr(1).toLowerCase();
		var icon: string;
		if (i == Scope.GLOBAL) {
			icon = 'fa fa-globe';
		}
		else if (i == Scope.SITE) {
			icon = 'fa fa-briefcase';
		}
		else if (i== Scope.AFFILIATE) {
			icon = 'fa fa-users';
		}
		scopes.push({
			id: +i,
			name: name,
			icon: icon
		});
	}

	export var roles = [];
	for (var i in Role) {
		var name = Role[i];
		if (typeof name == 'number') {
			continue;
		}
		name = name.charAt(0) + name.substr(1).toLowerCase();
		var icon: string;
		if (i == Role.ADMIN) {
			icon = 'fa fa-gavel';
		}
		else if (i == Role.SUPPORT) {
			icon = 'fa fa-comment-o';
		}
		roles.push({
			id: +i,
			name: name,
			icon: icon
		});
	}



	export class Profile extends BaseCollection<Profile> {
		static _forms;
		static db = new ProfileDb(Profile);
		static publish() { }

		public id: number;
		public userId: string;
		public username: string;
		public email: string;
		public firstName: string;
		public lastName: string;
		public country: OneRelation<Countries.Country>;
		public tracker: OneRelation<Affiliates.Tracker>;
		public affiliate: OneRelation<Affiliates.Affiliate>;
		public isAdmin: boolean;
		public isVip: VipStatus;
		public site: OneRelation<Backend.Site>;
		public siteAdmin: string;
		public phone: string;
		public city: string;
		public zip: string;
		public address: string;
		public password: any;
		public signupTime: Date;
		public dateOfBirth: Date;
		public refCode: string;
		public refPage: string;
		public wallets: ManyRelation<Banking.Wallet>;
		public roles: Access[];
		public otpEnabled: boolean;
		public playerGuideSeen: boolean;
		public disabled: boolean;
		public empId: string;
		public websites: string;
		public lastCountryCode: string;
		public elecUsername: string;

		static active(): Profile {
			return User.active() ? this.db.findOne(User.active().profile) : undefined;
		}

		static system(): Profile {
			return this.db.findOne({_id: '6uasKZiWDMDmiJaco'});
		}

		static create(userData: any, callback?: (err?, user?: Users.Profile) => void) {
			// server side
		}

		hasAccess(scopes: Scope[], roles: Role[], subject?: any): boolean {
			if (!this.roles || roles.length == 0) {
				return false;
			}
			var found = false;
			if (typeof subject === 'object') {
				subject = subject._id || subject.id;
			}
			this.roles.forEach(function(item){
				if (roles.indexOf(item.role) !== -1 && scopes.indexOf(item.scope) !== -1 && (!subject || item.subject == subject)) {
					found = true;
				}
			});
			return found;
		}

		getAccess(scopes: Scope[], roles: Role[], subject?: any): Access[] {
			if (!this.roles || roles.length == 0) {
				return [];
			}
			var found: Access[] = [];
			if (typeof subject === 'object') {
				subject = subject._id || subject.id;
			}
			this.roles.forEach(function(item){
				if (roles.indexOf(item.role) !== -1 && scopes.indexOf(item.scope) !== -1 && (!subject || item.subject == subject)) {
					found.push(item);
				}
			});
			return found;
		}

		accessSelector(selector: any, siteField: string = 'site', affiliateField: string = 'affiliate') {
			if (this.hasAccess([Scope.GLOBAL], [Role.ADMIN, Role.SUPPORT])) {
				return selector;
			}
			var sites: string[] = [];
			var affiliates: string[] = [];

			if (siteField !== null) {
				sites = this.getAccess([Scope.SITE], [Role.ADMIN, Role.SUPPORT]).map(function (access) {
					return access.subject;
				});
				if (siteField === '_id') {
					sites = sites.concat(this.getAccess([Scope.AFFILIATE], [Role.ADMIN, Role.SUPPORT]).map(function (access) {
						return Affiliates.Affiliate.db.findOne(access.subject).site.id;
					}));
				}
			}
			if (affiliateField !== null || siteField === '_id') {
				affiliates = this.getAccess([Scope.AFFILIATE], [Role.ADMIN, Role.SUPPORT]).map(function (access) {
					return access.subject;
				});
			}
			if (affiliateField === null) {
				selector[siteField] = {$in: sites};
			}
			if (siteField === null) {
				selector[affiliateField] = {$in: affiliates};
			}
			else {
				if (sites.length || affiliates.length) {
					var sel = [];
					selector = {$and: [selector, {$or: sel}]}
					if (sites.length > 0) {
						var item = {};
						item[siteField] = {$in: sites};
						sel.push(item);
					}
					if (affiliates.length > 0) {
						var item = {};
						item[affiliateField] = {$in: affiliates};
						sel.push(item);
					}
				}
				else {
					selector._id = 'NONE';
				}
			}
//			console.log(JSON.stringify(selector))
			return selector;
		}

