	export class Access extends BaseModel<Profile> {

		static db = new DataModel<Access>({
			source: Access,
			name: 'Users.Access'
		});

		role: Role;
		scope: Scope;
		subject: string;

		roleName(): string {
			return roles[this.role].name;
		}

		roleIcon(): string {
			return roles[this.role].icon;
		}

		scopeName(): string {
			return scopes[this.scope].name;
		}

		scopeIcon(): string {
			return scopes[this.scope].icon;
		}

		subjectName(): string {
			if (this.scope == Scope.GLOBAL) {
				return null;
			}
			else if (this.scope == Scope.SITE) {
				var site = Backend.Site.db.findOne(this.subject);
				if (!site) {
					return 'unknown site';
				}
				return site.name;
			}
			else if (this.scope == Scope.AFFILIATE) {
				var aff = Affiliates.Affiliate.db.findOne(this.subject);
				if (!aff) {
					return 'unknown affiliate';
				}
				return aff.site.fetch().name + ' ' + aff.name;
			}

		}

	}

