	User.checkUsernameAvailability = function (username, site?, callback?) {
		Meteor.call('checkUsernameAvailability', username, site, (err, res) => callback(res));
		return false;
	};

	User.checkEmailAvailability = function (email, site?, callback?) {
		Meteor.call('checkEmailAvailability', email, site, (err, res) => callback(res));
		return false;
	};

	User.loginWithPassword = function(username: string, password: string, callback: (err?) => void) {
		Meteor.loginWithPassword(Meteor.settings.app + '+' + username, password, callback);
	};

	User.forgotPassword = function(email: string, callback: (err?) => void) {
		Meteor.call('Users/forgotPassword', email, callback);
	};


	// refCode init

	Meteor.startup(function() {

		var qs = parseQueryString(window.location.search);

		// query string sessKey var login
		if (qs.sessKey) {
			var targetUrl = window.location.pathname;
			Meteor.loginWithToken(qs.sessKey, function(err?){
				if (err) {
					console.log(err);
					return;
				}
				Pages.go(targetUrl);
			});
		}

		if (qs.refCode || qs.ref) {
			$.cookie('refCode', qs.refCode || qs.ref, {expires: 7});
		}
	});
